import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

function RoleSelection() {
  const { updateUserRole, currentUser } = useAuth();
  const [selectedRole, setSelectedRole] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const roles = [
    {
      id: 'ngo',
      title: 'NGO',
      description: 'Non-Governmental Organization working on social causes',
      icon: 'ðŸ¤',
      color: 'bg-green-100 border-green-300 hover:bg-green-200'
    },
    {
      id: 'startup',
      title: 'Startup',
      description: 'Technology startup or social enterprise',
      icon: 'ðŸš€',
      color: 'bg-blue-100 border-blue-300 hover:bg-blue-200'
    },
    {
      id: 'government',
      title: 'Government',
      description: 'Government body or public institution',
      icon: 'ðŸ›ï¸',
      color: 'bg-purple-100 border-purple-300 hover:bg-purple-200'
    }
  ];

  const handleRoleSubmit = async () => {
    if (!selectedRole) return;

    try {
      setLoading(true);
      await updateUserRole(selectedRole);
      navigate('/');
    } catch (error) {
      console.error('Error updating role:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 to-green-50">
      <div className="max-w-2xl w-full space-y-8 p-8">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Welcome to SDG Bridge</h1>
          <p className="text-lg text-gray-600 mb-8">
            Hi {currentUser?.displayName}! Please select your organization type to get started.
          </p>
        </div>

        <div className="card">
          <h2 className="text-xl font-semibold text-gray-900 mb-6 text-center">
            What type of organization do you represent?
          </h2>

          <div className="grid gap-4 mb-6">
            {roles.map((role) => (
              <div
                key={role.id}
                className={`p-4 border-2 rounded-lg cursor-pointer transition-all ${
                  selectedRole === role.id
                    ? 'border-primary-500 bg-primary-50'
                    : role.color
                }`}
                onClick={() => setSelectedRole(role.id)}
              >
                <div className="flex items-center space-x-4">
                  <div className="text-3xl">{role.icon}</div>
                  <div className="flex-1">
                    <h3 className="text-lg font-medium text-gray-900">{role.title}</h3>
                    <p className="text-sm text-gray-600">{role.description}</p>
                  </div>
                  <div className="flex-shrink-0">
                    <div className={`w-4 h-4 rounded-full border-2 ${
                      selectedRole === role.id
                        ? 'bg-primary-500 border-primary-500'
                        : 'border-gray-300'
                    }`}></div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button
            onClick={handleRoleSubmit}
            disabled={!selectedRole || loading}
            className="w-full btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Setting up your account...' : 'Continue'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default RoleSelection;
