import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Welcome = () => {
  const { currentUser } = useAuth();

  // Add custom animations
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes float {
        0%, 100% { transform: translateY(0px); }
        50% { transform: translateY(-20px); }
      }

      @keyframes fade-in-up {
        from {
          opacity: 0;
          transform: translateY(30px);
        }
        to {
          opacity: 1;
          transform: translateY(0);
        }
      }

      @keyframes fade-in {
        from { opacity: 0; }
        to { opacity: 1; }
      }

      .animate-float {
        animation: float 6s ease-in-out infinite;
      }

      .animate-fade-in-up {
        animation: fade-in-up 0.8s ease-out;
      }

      .animate-fade-in {
        animation: fade-in 1s ease-out;
      }

      .animation-delay-300 {
        animation-delay: 0.3s;
        animation-fill-mode: both;
      }

      .animation-delay-500 {
        animation-delay: 0.5s;
        animation-fill-mode: both;
      }

      .animation-delay-700 {
        animation-delay: 0.7s;
        animation-fill-mode: both;
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-900 via-purple-900 to-indigo-900 relative overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
        <div className="absolute top-40 left-40 w-80 h-80 bg-pink-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>

        {/* Floating particles */}
        <div className="absolute top-1/4 left-1/4 w-2 h-2 bg-white rounded-full opacity-60 animate-ping animation-delay-1000"></div>
        <div className="absolute top-3/4 right-1/4 w-1 h-1 bg-blue-300 rounded-full opacity-80 animate-pulse animation-delay-3000"></div>
        <div className="absolute top-1/2 left-3/4 w-3 h-3 bg-purple-300 rounded-full opacity-50 animate-bounce animation-delay-5000"></div>
        <div className="absolute bottom-1/4 left-1/2 w-2 h-2 bg-pink-300 rounded-full opacity-70 animate-ping animation-delay-2000"></div>
      </div>

      {/* Hero Section */}
      <div className="relative z-10 max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20">
        <div className="text-center animate-fade-in-up">
          <div className="mb-8">
            <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full mb-6 shadow-2xl animate-float">
              <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
              </svg>
            </div>
          </div>

          <h1 className="text-5xl md:text-7xl font-extrabold text-white mb-6 animate-fade-in animation-delay-300">
            Bridge the Gap to
            <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent block mt-2">
              Sustainable Impact
            </span>
          </h1>

          <p className="text-xl md:text-2xl text-blue-100 mb-12 max-w-4xl mx-auto leading-relaxed animate-fade-in animation-delay-500">
            Connect NGOs, startups, and government agencies working on UN Sustainable Development Goals. 
            Collaborate, share resources, and create meaningful change together.
          </p>

          <div className="flex flex-col sm:flex-row gap-6 justify-center animate-fade-in animation-delay-700">
            <Link
              to="/projects"
              className="group relative inline-flex items-center justify-center px-8 py-4 text-lg font-bold text-white bg-gradient-to-r from-blue-600 via-purple-600 to-blue-700 rounded-xl shadow-2xl hover:shadow-3xl transform hover:scale-105 transition-all duration-300"
            >
              <span className="absolute inset-0 w-full h-full bg-gradient-to-r from-blue-400 to-purple-400 rounded-xl blur opacity-30 group-hover:opacity-50 transition-opacity duration-300"></span>
              <span className="relative flex items-center">
                Explore Projects
                <svg className="ml-2 h-5 w-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 7l5 5m0 0l-5 5m5-5H6" />
                </svg>
              </span>
            </Link>

            {!currentUser && (
              <Link
                to="/login"
                className="group relative inline-flex items-center justify-center px-8 py-4 text-lg font-bold text-white bg-white/10 border-2 border-white/30 rounded-xl backdrop-blur-sm hover:bg-white/20 hover:border-white/50 transition-all duration-300 transform hover:scale-105"
              >
                <span className="relative flex items-center">
                  Join the Community
                  <svg className="ml-2 h-5 w-5 group-hover:translate-x-1 transition-transform duration-300" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 8l4 4m0 0l-4 4m4-4H3" />
                  </svg>
                </span>
              </Link>
            )}
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="bg-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">How SDG Bridge Works</h2>
            <p className="text-lg text-gray-600">Connecting organizations for sustainable development</p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            <div className="text-center p-6">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">Discover Projects</h3>
              <p className="text-gray-600">Browse projects aligned with UN Sustainable Development Goals from organizations worldwide.</p>
            </div>

            <div className="text-center p-6">
              <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">Collaborate</h3>
              <p className="text-gray-600">Connect with like-minded organizations and request collaboration on impactful projects.</p>
            </div>

            <div className="text-center p-6">
              <div className="w-16 h-16 bg-purple-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4M7.835 4.697a3.42 3.42 0 001.946-.806 3.42 3.42 0 014.438 0 3.42 3.42 0 001.946.806 3.42 3.42 0 013.138 3.138 3.42 3.42 0 00.806 1.946 3.42 3.42 0 010 4.438 3.42 3.42 0 00-.806 1.946 3.42 3.42 0 01-3.138 3.138 3.42 3.42 0 00-1.946.806 3.42 3.42 0 01-4.438 0 3.42 3.42 0 00-1.946-.806 3.42 3.42 0 01-3.138-3.138 3.42 3.42 0 00-.806-1.946 3.42 3.42 0 010-4.438 3.42 3.42 0 00.806-1.946 3.42 3.42 0 013.138-3.138z" />
                </svg>
              </div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">Create Impact</h3>
              <p className="text-gray-600">Work together to achieve the UN Sustainable Development Goals and create lasting change.</p>
            </div>
          </div>
        </div>
      </div>

      {/* SDG Section */}
      <div className="bg-gray-50 py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-16">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">UN Sustainable Development Goals</h2>
            <p className="text-lg text-gray-600">Projects aligned with the 17 SDGs for a better world</p>
          </div>

          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
            {[
              { num: 1, title: "No Poverty", color: "bg-red-500" },
              { num: 2, title: "Zero Hunger", color: "bg-yellow-500" },
              { num: 3, title: "Good Health", color: "bg-green-500" },
              { num: 4, title: "Quality Education", color: "bg-red-600" },
              { num: 5, title: "Gender Equality", color: "bg-orange-500" },
              { num: 6, title: "Clean Water", color: "bg-blue-400" },
              { num: 7, title: "Clean Energy", color: "bg-yellow-400" },
              { num: 8, title: "Economic Growth", color: "bg-red-400" },
              { num: 9, title: "Innovation", color: "bg-orange-600" },
              { num: 10, title: "Reduced Inequality", color: "bg-pink-500" },
              { num: 11, title: "Sustainable Cities", color: "bg-yellow-600" },
              { num: 12, title: "Responsible Consumption", color: "bg-yellow-700" },
              { num: 13, title: "Climate Action", color: "bg-green-600" },
              { num: 14, title: "Life Below Water", color: "bg-blue-500" },
              { num: 15, title: "Life on Land", color: "bg-green-700" },
              { num: 16, title: "Peace & Justice", color: "bg-blue-600" },
              { num: 17, title: "Partnerships", color: "bg-blue-700" }
            ].map((sdg) => (
              <div key={sdg.num} className={`${sdg.color} text-white p-4 rounded-lg text-center`}>
                <div className="text-2xl font-bold mb-1">{sdg.num}</div>
                <div className="text-xs">{sdg.title}</div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="bg-blue-600 py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl font-bold text-white mb-4">Ready to Make an Impact?</h2>
          <p className="text-xl text-blue-100 mb-8">Join thousands of organizations working towards sustainable development</p>
          {!currentUser && (
            <Link
              to="/login"
              className="bg-white text-blue-600 px-8 py-4 rounded-lg text-lg font-semibold hover:bg-gray-100 transition-colors"
            >
              Get Started Today
            </Link>
          )}
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-gray-900 text-white py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h3 className="text-2xl font-bold mb-4">SDG Bridge</h3>
            <p className="text-gray-400 mb-4">Connecting organizations for sustainable impact</p>
            <div className="flex justify-center space-x-6">
              <Link to="/projects" className="text-gray-400 hover:text-white transition-colors">
                Explore Projects
              </Link>
              {!currentUser && (
                <Link to="/login" className="text-gray-400 hover:text-white transition-colors">
                  Join Us
                </Link>
              )}
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Welcome;
