import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';

function RoleSelection() {
  const { updateUserRole, currentUser } = useAuth();
  const [selectedRole, setSelectedRole] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const roles = [
    {
      id: 'ngo',
      title: 'NGO',
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 2 2 0 014 0z" />
        </svg>
      ),
      description: 'Non-profit organization working on sustainable development',
      color: 'from-green-400 to-blue-500'
    },
    {
      id: 'startup',
      title: 'Startup',
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
        </svg>
      ),
      description: 'Innovation-driven company creating sustainable solutions',
      color: 'from-purple-400 to-pink-500'
    },
    {
      id: 'university',
      title: 'University',
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l9-5-9-5-9 5 9 5z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14l6.16-3.422a12.083 12.083 0 01.665 6.479A11.952 11.952 0 0012 20.055a11.952 11.952 0 00-6.824-2.998 12.078 12.078 0 01.665-6.479L12 14z" />
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 14V9.5l-3-1.5" />
        </svg>
      ),
      description: 'Academic institution promoting research and education',
      color: 'from-blue-400 to-indigo-500'
    },
    {
      id: 'government',
      title: 'Government',
      icon: (
        <svg className="w-8 h-8" fill="none" stroke="currentColor" viewBox="0 0 24 24">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
        </svg>
      ),
      description: 'Government agency or public sector organization',
      color: 'from-red-400 to-orange-500'
    }
  ];

  const handleRoleSubmit = async () => {
    if (!selectedRole) return;

    try {
      setLoading(true);
      await updateUserRole(selectedRole);
      navigate('/');
    } catch (error) {
      console.error('Error updating role:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-50 via-purple-50 to-green-50 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-gradient-to-br from-blue-400/20 to-purple-400/20 rounded-full blur-3xl"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-gradient-to-br from-green-400/20 to-blue-400/20 rounded-full blur-3xl"></div>
      </div>

      <div className="max-w-2xl w-full space-y-8 p-8 relative z-10">
        <div className="text-center animate-fade-in">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full mb-6 shadow-xl animate-bounce">
            <svg className="w-10 h-10 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3.055 11H5a2 2 0 012 2v1a2 2 0 002 2 2 2 0 012 2v2.945M8 3.935V5.5A2.5 2.5 0 0010.5 8h.5a2 2 0 012 2 2 2 0 104 0 2 2 0 012-2h1.064M15 20.488V18a2 2 0 012-2h3.064M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
          </div>
          <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-green-600 bg-clip-text text-transparent mb-4">
            Welcome to SDG Bridge
          </h1>
          <p className="text-xl text-gray-700 mb-8 font-medium">
            Hi <span className="text-blue-600 font-semibold">{currentUser?.displayName}</span>! Please select your organization type to get started.
          </p>
        </div>

        <div className="bg-white/20 backdrop-blur-lg border border-white/30 rounded-3xl p-8 shadow-2xl hover:shadow-3xl transition-all duration-500 animate-slide-up">
          <h2 className="text-2xl font-bold text-gray-800 mb-8 text-center flex items-center justify-center">
            <span className="mr-3">üéØ</span>
            What type of organization do you represent?
          </h2>

          <div className="grid gap-6 mb-8">
            {roles.map((role, index) => (
              <div
                key={role.id}
                className={`group p-6 border-2 rounded-2xl cursor-pointer transition-all duration-300 transform hover:scale-105 hover:shadow-xl ${
                  selectedRole === role.id
                    ? 'border-blue-500/50 bg-gradient-to-r from-blue-500/20 to-purple-500/20 backdrop-blur-sm shadow-xl scale-105'
                    : 'border-white/40 bg-white/30 backdrop-blur-sm hover:bg-white/40 hover:border-white/60'
                }`}
                onClick={() => setSelectedRole(role.id)}
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="flex items-center space-x-6">
                  <div className={`text-4xl p-3 rounded-full transition-all duration-300 ${
                    selectedRole === role.id 
                      ? 'bg-gradient-to-br from-blue-500 to-purple-600 shadow-lg transform scale-110' 
                      : 'bg-white/40 group-hover:bg-white/60 group-hover:scale-110'
                  }`}>
                    {role.icon}
                  </div>
                  <div className="flex-1">
                    <h3 className={`text-xl font-bold transition-colors duration-300 ${
                      selectedRole === role.id 
                        ? 'text-blue-700' 
                        : 'text-gray-800 group-hover:text-blue-600'
                    }`}>
                      {role.title}
                    </h3>
                    <p className={`text-sm mt-1 transition-colors duration-300 ${
                      selectedRole === role.id 
                        ? 'text-blue-600' 
                        : 'text-gray-600 group-hover:text-gray-700'
                    }`}>
                      ‚ú® {role.description}
                    </p>
                  </div>
                  <div className="flex-shrink-0">
                    <div className={`w-6 h-6 rounded-full border-3 transition-all duration-300 flex items-center justify-center ${
                      selectedRole === role.id
                        ? 'bg-gradient-to-br from-blue-500 to-purple-600 border-blue-500 shadow-lg'
                        : 'border-gray-400 group-hover:border-blue-400 group-hover:scale-110'
                    }`}>
                      {selectedRole === role.id && (
                        <span className="text-white text-xs">‚úì</span>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button
            onClick={handleRoleSubmit}
            disabled={!selectedRole || loading}
            className={`w-full py-4 px-8 rounded-2xl font-bold text-lg transition-all duration-300 transform flex items-center justify-center space-x-3 ${
              selectedRole && !loading
                ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white hover:from-blue-600 hover:to-purple-700 hover:scale-105 shadow-xl hover:shadow-2xl'
                : 'bg-gray-300/50 text-gray-500 cursor-not-allowed backdrop-blur-sm'
            } disabled:opacity-50 disabled:transform-none`}
          >
            <span className="text-xl">
              {loading ? '‚è≥' : selectedRole ? 'üöÄ' : 'üîí'}
            </span>
            <span>
              {loading ? 'Setting up your account...' : selectedRole ? 'Continue to Dashboard' : 'Select a role to continue'}
            </span>
          </button>
        </div>
      </div>
    </div>
  );
}

export default RoleSelection;
