import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { currentUser, userProfile, logout } = useAuth();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'ngo': return 'bg-green-100 text-green-800';
      case 'startup': return 'bg-blue-100 text-blue-800';
      case 'government': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <nav className="bg-white shadow-sm border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-2">
              <div className="text-2xl font-bold text-primary-600">SDG Bridge</div>
            </Link>

            <div className="hidden md:ml-8 md:flex md:space-x-8">
              <Link
                to="/"
                className={`inline-flex items-center px-1 pt-1 text-sm font-medium border-b-2 ${
                  isActive('/')
                    ? 'border-primary-500 text-primary-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                Dashboard
              </Link>
              <Link
                to="/projects"
                className={`inline-flex items-center px-1 pt-1 text-sm font-medium border-b-2 ${
                  isActive('/projects')
                    ? 'border-primary-500 text-primary-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                Browse Projects
              </Link>
              <Link
                to="/post-project"
                className={`inline-flex items-center px-1 pt-1 text-sm font-medium border-b-2 ${
                  isActive('/post-project')
                    ? 'border-primary-500 text-primary-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                Post Project
              </Link>
            </div>
          </div>

          <div className="flex items-center space-x-4">
            {userProfile?.role && (
              <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRoleColor(userProfile.role)}`}>
                {userProfile.role.toUpperCase()}
              </span>
            )}

            <div className="flex items-center space-x-3">
              {currentUser?.photoURL && (
                <img
                  className="h-8 w-8 rounded-full"
                  src={currentUser.photoURL}
                  alt={currentUser.displayName}
                />
              )}
              <span className="text-sm font-medium text-gray-700">
                {currentUser?.displayName}
              </span>
              <button
                onClick={handleLogout}
                className="text-sm text-gray-500 hover:text-gray-700 transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      <div className="md:hidden">
        <div className="pt-2 pb-3 space-y-1">
          <Link
            to="/"
            className={`block pl-3 pr-4 py-2 text-base font-medium ${
              isActive('/')
                ? 'bg-primary-50 border-r-4 border-primary-500 text-primary-700'
                : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'
            }`}
          >
            Dashboard
          </Link>
          <Link
            to="/projects"
            className={`block pl-3 pr-4 py-2 text-base font-medium ${
              isActive('/projects')
                ? 'bg-primary-50 border-r-4 border-primary-500 text-primary-700'
                : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'
            }`}
          >
            Browse Projects
          </Link>
          <Link
            to="/post-project"
            className={`block pl-3 pr-4 py-2 text-base font-medium ${
              isActive('/post-project')
                ? 'bg-primary-50 border-r-4 border-primary-500 text-primary-700'
                : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'
            }`}
          >
            Post Project
          </Link>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
