import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { db } from '../firebase';
import { collection, getDocs, doc, updateDoc, arrayRemove, arrayUnion } from 'firebase/firestore';

function Communications() {
  const { userProfile } = useAuth();
  const [sentRequests, setSentRequests] = useState([]);
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('received');
  const [updatingRequest, setUpdatingRequest] = useState({});

  useEffect(() => {
    if (userProfile?.uid) {
      fetchCommunications();
    }
  }, [userProfile]);

  const fetchCommunications = async () => {
    try {
      const projectsSnapshot = await getDocs(collection(db, 'projects'));
      const projectsData = projectsSnapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      }));

      // Find requests sent by current user
      const sent = [];
      projectsData.forEach(project => {
        if (project.collaborationRequests) {
          project.collaborationRequests.forEach(request => {
            if (request.userId === userProfile.uid) {
              sent.push({
                ...request,
                projectId: project.id,
                projectTitle: project.title,
                projectOwner: project.createdByName,
                projectOwnerRole: project.createdByRole
              });
            }
          });
        }
      });

      // Find requests received by current user (for their projects)
      const received = [];
      projectsData.forEach(project => {
        if (project.createdBy === userProfile.uid && project.collaborationRequests) {
          project.collaborationRequests.forEach(request => {
            received.push({
              ...request,
              projectId: project.id,
              projectTitle: project.title
            });
          });
        }
      });

      setSentRequests(sent.sort((a, b) => new Date(b.requestedAt) - new Date(a.requestedAt)));
      setReceivedRequests(received.sort((a, b) => new Date(b.requestedAt) - new Date(a.requestedAt)));
    } catch (error) {
      console.error('Error fetching communications:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleRequestResponse = async (projectId, request, action) => {
    const requestKey = `${projectId}-${request.userId}`;
    try {
      setUpdatingRequest(prev => ({ ...prev, [requestKey]: true }));

      // Update the request status
      const updatedRequest = { ...request, status: action };

      // Remove old request and add updated one
      await updateDoc(doc(db, 'projects', projectId), {
        collaborationRequests: arrayRemove(request)
      });

      await updateDoc(doc(db, 'projects', projectId), {
        collaborationRequests: arrayUnion(updatedRequest)
      });

      // Update local state
      setReceivedRequests(prev => 
        prev.map(req => 
          req.projectId === projectId && req.userId === request.userId
            ? { ...req, status: action }
            : req
        )
      );

      alert(`Request ${action} successfully!`);
    } catch (error) {
      console.error(`Error ${action}ing request:`, error);
      alert(`Error ${action}ing request. Please try again.`);
    } finally {
      setUpdatingRequest(prev => ({ ...prev, [requestKey]: false }));
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'accepted': return 'bg-green-100 text-green-800';
      case 'rejected': return 'bg-red-100 text-red-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'ngo': return 'bg-green-100 text-green-800';
      case 'startup': return 'bg-blue-100 text-blue-800';
      case 'government': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-8"></div>
          <div className="grid gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-32 bg-gray-200 rounded-lg"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Communications</h1>
        <p className="text-gray-600 mt-2">
          Manage your collaboration requests and responses.
        </p>
      </div>

      {/* Tabs */}
      <div className="border-b border-gray-200 mb-8">
        <nav className="-mb-px flex space-x-8">
          <button
            onClick={() => setActiveTab('received')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'received'
                ? 'border-blue-500 text-blue-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            Received Requests
            {receivedRequests.length > 0 && (
              <span className="ml-2 bg-blue-100 text-blue-600 py-0.5 px-2 rounded-full text-xs">
                {receivedRequests.filter(req => req.status === 'pending').length}
              </span>
            )}
          </button>
          <button
            onClick={() => setActiveTab('sent')}
            className={`py-2 px-1 border-b-2 font-medium text-sm ${
              activeTab === 'sent'
                ? 'border-blue-500 text-blue-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
            }`}
          >
            Sent Requests
            {sentRequests.length > 0 && (
              <span className="ml-2 bg-gray-100 text-gray-600 py-0.5 px-2 rounded-full text-xs">
                {sentRequests.length}
              </span>
            )}
          </button>
        </nav>
      </div>

      {/* Received Requests Tab */}
      {activeTab === 'received' && (
        <div className="space-y-6">
          {receivedRequests.length > 0 ? (
            receivedRequests.map((request) => {
              const requestKey = `${request.projectId}-${request.userId}`;
              return (
                <div key={requestKey} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-3">
                        <h3 className="text-lg font-semibold text-gray-900">
                          {request.projectTitle}
                        </h3>
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(request.status)}`}>
                          {request.status?.toUpperCase() || 'PENDING'}
                        </span>
                      </div>

                      <div className="flex items-center space-x-4 text-sm text-gray-600 mb-4">
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                          </svg>
                          <span>{request.userName}</span>
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRoleColor(request.userRole)}`}>
                            {request.userRole?.toUpperCase()}
                          </span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                          </svg>
                          <span>{request.userEmail}</span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                          </svg>
                          <span>{new Date(request.requestedAt).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>

                    {request.status === 'pending' && (
                      <div className="lg:ml-6 lg:flex-shrink-0">
                        <div className="flex space-x-3">
                          <button
                            onClick={() => handleRequestResponse(request.projectId, request, 'accepted')}
                            disabled={updatingRequest[requestKey]}
                            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                          >
                            {updatingRequest[requestKey] ? 'Accepting...' : 'Accept'}
                          </button>
                          <button
                            onClick={() => handleRequestResponse(request.projectId, request, 'rejected')}
                            disabled={updatingRequest[requestKey]}
                            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                          >
                            {updatingRequest[requestKey] ? 'Rejecting...' : 'Reject'}
                          </button>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              );
            })
          ) : (
            <div className="text-center py-12">
              <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2 2v-5m16 0h-2.586a1 1 0 00-.707.293l-2.414 2.414a1 1 0 01-.707.293h-4.172a1 1 0 01-.707-.293l-2.414-2.414A1 1 0 009.586 13H7" />
              </svg>
              <h3 className="mt-2 text-sm font-medium text-gray-900">No collaboration requests</h3>
              <p className="mt-1 text-sm text-gray-500">
                You haven't received any collaboration requests yet.
              </p>
            </div>
          )}
        </div>
      )}

      {/* Sent Requests Tab */}
      {activeTab === 'sent' && (
        <div className="space-y-6">
          {sentRequests.length > 0 ? (
            sentRequests.map((request) => {
              const requestKey = `${request.projectId}-${request.userId}`;
              return (
                <div key={requestKey} className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
                  <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-3 mb-3">
                        <h3 className="text-lg font-semibold text-gray-900">
                          {request.projectTitle}
                        </h3>
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(request.status)}`}>
                          {request.status?.toUpperCase() || 'PENDING'}
                        </span>
                      </div>

                      <div className="flex items-center space-x-4 text-sm text-gray-600 mb-4">
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                          </svg>
                          <span>Project by {request.projectOwner}</span>
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRoleColor(request.projectOwnerRole)}`}>
                            {request.projectOwnerRole?.toUpperCase()}
                          </span>
                        </div>
                        <div className="flex items-center space-x-1">
                          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                          </svg>
                          <span>Sent {new Date(request.requestedAt).toLocaleDateString()}</span>
                        </div>
                      </div>
                    </div>

                    <div className="lg:ml-6 lg:flex-shrink-0">
                      <div className="text-center">
                        {request.status === 'accepted' && (
                          <div className="text-green-600 font-medium">
                            ✓ Request Accepted
                          </div>
                        )}
                        {request.status === 'rejected' && (
                          <div className="text-red-600 font-medium">
                            ✗ Request Rejected
                          </div>
                        )}
                        {(!request.status || request.status === 'pending') && (
                          <div className="text-yellow-600 font-medium">
                            ⏳ Awaiting Response
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              );
            })
          ) : (
            <div className="text-center py-12">
              <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
              </svg>
              <h3 className="mt-2 text-sm font-medium text-gray-900">No requests sent</h3>
              <p className="mt-1 text-sm text-gray-500">
                You haven't sent any collaboration requests yet.
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default Communications;
