import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { currentUser, userProfile, logout } = useAuth();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'ngo': return 'bg-gradient-to-r from-green-400 to-emerald-500 text-white shadow-lg shadow-green-500/25';
      case 'startup': return 'bg-gradient-to-r from-blue-400 to-cyan-500 text-white shadow-lg shadow-blue-500/25';
      case 'government': return 'bg-gradient-to-r from-purple-400 to-violet-500 text-white shadow-lg shadow-purple-500/25';
      default: return 'bg-gradient-to-r from-gray-400 to-slate-500 text-white shadow-lg shadow-gray-500/25';
    }
  };

  return (
    <nav className="bg-white/80 backdrop-blur-lg shadow-xl border-b border-white/20 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-3 hover:scale-105 transition-all duration-300 cursor-pointer group">
              <div className="relative">
                <img 
                  src="/favicon.png" 
                  alt="SDG Bridge Logo" 
                  className="h-10 w-10 object-contain group-hover:rotate-12 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </div>
              <div className="text-2xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
                SDG Bridge
              </div>
            </Link>

            {currentUser && (
              <div className="hidden md:ml-8 md:flex md:space-x-6">
                <Link
                  to="/dashboard"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/dashboard')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <span>📊</span>
                  <span>Dashboard</span>
                </Link>
                <Link
                  to="/projects"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/projects')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                  <span>Browse Projects</span>
                </Link>
                <Link
                  to="/post-project"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/post-project')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                  </svg>
                  <span>Post Project</span>
                </Link>
                <Link
                  to="/communications"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/communications')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
                  </svg>
                  <span>Communications</span>
                </Link>
              </div>
            )}
          </div>

          <div className="flex items-center space-x-3">
            {!currentUser && (
              <div className="flex items-center space-x-3">
                <Link
                  to="/"
                  className="inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105 rounded-xl transition-all duration-300"
                >
                  <span>🏠</span>
                  <span>Home</span>
                </Link>
                <Link
                  to="/projects"
                  className="inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105 rounded-xl transition-all duration-300"
                >
                  <span>🚀</span>
                  <span>Explore Projects</span>
                </Link>
                <Link
                  to="/login"
                  className="inline-flex items-center space-x-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white px-6 py-2 rounded-xl hover:from-blue-600 hover:to-purple-700 hover:scale-105 transition-all duration-300 shadow-lg shadow-blue-500/25 font-medium"
                >
                  <span>✨</span>
                  <span>Login</span>
                </Link>
              </div>
            )}

            {currentUser && (
              <>
                {userProfile?.role && (
                  <span className={`px-3 py-1 text-xs font-bold rounded-full transform hover:scale-105 transition-all duration-300 ${getRoleColor(userProfile.role)}`}>
                    {userProfile.role === 'ngo' ? 'NGO' : 
                     userProfile.role === 'startup' ? 'STARTUP' : 
                     userProfile.role === 'government' ? 'GOVERNMENT' : 
                     userProfile.role.toUpperCase()}
                  </span>
                )}

                <div className="flex items-center space-x-3 bg-white/30 backdrop-blur-sm rounded-xl px-4 py-2 border border-white/20 shadow-lg">
                  {currentUser?.photoURL && (
                    <img
                      className="h-8 w-8 rounded-full ring-2 ring-white/50 hover:ring-white transition-all duration-300"
                      src={currentUser.photoURL}
                      alt={currentUser.displayName}
                    />
                  )}
                  <span className="text-sm font-medium text-gray-700 hidden sm:block">
                    {currentUser?.displayName}
                  </span>
                  <button
                    onClick={handleLogout}
                    className="inline-flex items-center space-x-1 text-sm text-gray-500 hover:text-red-600 hover:bg-red-50 px-3 py-1 rounded-lg transition-all duration-300 hover:scale-105"
                  >
                    <span>🚪</span>
                    <span className="hidden sm:inline">Logout</span>
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      {currentUser && (
        <div className="md:hidden bg-white/50 backdrop-blur-lg border-t border-white/20">
          <div className="pt-2 pb-3 space-y-2 px-4">
            <Link
              to="/dashboard"
              className={`flex items-center space-x-3 px-4 py-3 text-base font-medium rounded-xl transition-all duration-300 ${
                isActive('/dashboard')
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                  : 'text-gray-600 hover:bg-white/50 hover:text-gray-800'
              }`}
            >
              <span>📊</span>
              <span>Dashboard</span>
            </Link>
            <Link
              to="/projects"
              className={`flex items-center space-x-3 px-4 py-3 text-base font-medium rounded-xl transition-all duration-300 ${
                isActive('/projects')
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                  : 'text-gray-600 hover:bg-white/50 hover:text-gray-800'
              }`}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
              <span>Browse Projects</span>
            </Link>
            <Link
              to="/post-project"
              className={`flex items-center space-x-3 px-4 py-3 text-base font-medium rounded-xl transition-all duration-300 ${
                isActive('/post-project')
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                  : 'text-gray-600 hover:bg-white/50 hover:text-gray-800'
              }`}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
              </svg>
              <span>Post Project</span>
            </Link>
            <Link
              to="/communications"
              className={`flex items-center space-x-3 px-4 py-3 text-base font-medium rounded-xl transition-all duration-300 ${
                isActive('/communications')
                  ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg'
                  : 'text-gray-600 hover:bg-white/50 hover:text-gray-800'
              }`}
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
              </svg>
              <span>Communications</span>
            </Link>
          </div>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
