import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Navbar() {
  const { currentUser, userProfile, logout } = useAuth();
  const location = useLocation();

  const isActive = (path) => location.pathname === path;

  const handleLogout = async () => {
    try {
      await logout();
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'ngo': return 'bg-gradient-to-r from-green-400 to-emerald-500 text-white shadow-lg shadow-green-500/25';
      case 'startup': return 'bg-gradient-to-r from-blue-400 to-cyan-500 text-white shadow-lg shadow-blue-500/25';
      case 'government': return 'bg-gradient-to-r from-purple-400 to-violet-500 text-white shadow-lg shadow-purple-500/25';
      default: return 'bg-gradient-to-r from-gray-400 to-slate-500 text-white shadow-lg shadow-gray-500/25';
    }
  };

  return (
    <nav className="bg-white/80 backdrop-blur-lg shadow-xl border-b border-white/20 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center space-x-3 hover:scale-105 transition-all duration-300 cursor-pointer group">
              <div className="relative">
                <img 
                  src="/favicon.png" 
                  alt="SDG Bridge Logo" 
                  className="h-10 w-10 object-contain group-hover:rotate-12 transition-transform duration-300"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full opacity-0 group-hover:opacity-20 transition-opacity duration-300"></div>
              </div>
              <div className="text-2xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-indigo-600 bg-clip-text text-transparent">
                SDG Bridge
              </div>
            </Link>

            {currentUser && (
              <div className="hidden md:ml-8 md:flex md:space-x-6">
                <Link
                  to="/dashboard"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/dashboard')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <span>📊</span>
                  <span>Dashboard</span>
                </Link>
                <Link
                  to="/projects"
                  className={`inline-flex items-center space-x-2 px-4 py-2 text-sm font-medium rounded-xl transition-all duration-300 ${
                    isActive('/projects')
                      ? 'bg-gradient-to-r from-blue-500 to-purple-600 text-white shadow-lg shadow-blue-500/25 transform scale-105'
                      : 'text-gray-600 hover:bg-white/50 hover:backdrop-blur-sm hover:text-gray-800 hover:shadow-md hover:scale-105'
                  }`}
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
